var feedback=document.querySelector(".address__btn"),popup=document.querySelector(".modal"),popupwrap=document.querySelector(".modal-wrap"),popupclose=document.querySelector(".modal__btn-close"),popupform=document.querySelector(".modal__form"),username=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),text=popup.querySelector("[name=text]");feedback.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),popupwrap.classList.add("modal-wrap__show"),username.focus()}),popupclose.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popupwrap.classList.remove("modal-wrap__show"),popup.classList.remove("modal-error")}),popupwrap.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popupwrap.classList.remove("modal-wrap__show")}),popupform.addEventListener("submit",function(e){username.value&&email.value&&text.value||(e.preventDefault(),popup.classList.add("modal-error"))});